import React from "react";
import Header from "../Header";
import Category from "../Categories/Category";
import CategoryContainer from "../Categories/CategoryContainer";
import "../styles/Home.css";


class CategoryPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      bills: [],
      contracts: []
    };
  }

  fetchCategories() {
    fetch("/categories")
      .then(res => res.json())
      .then(categories => this.setState({ categories: categories }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchCategories();
    this.fetchBills();
    this.fetchContracts();
  }

  render() {
    return (
      <div className="content-container">
        <Header
          title="Kategorien"
          text="Herzlich Willkommen, hier sind die Kategorien, zu denen Ihre Dokumente zugewiesen werden.
         Sie können neue Kategorien hinzufügen oder löschen."
        />
        <div className="content-container">
          <ul>
            {this.state.categories.map(category => {
              return (
                <Category
                  key={category.id}
                  id={category.id}
                  name={category.name}
                ></Category>
              );
            })}
          </ul>
        </div>
        <CategoryContainer categories={this.state.categories} />
      </div>
    );
  }
}

export default CategoryPage;
