import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class EditBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: {
        id: "",
        userId: "",
        name: "",
        kosten: "",
        beschreibung: ""
      },
      billId: this.props.location.state
    };
  }
  // PUT REQUEST KLAPPT NOCH NICHT
  handleEditSubmit = e => {
    e.preventDefault();
    let obj = {
      name: this.state.bills.name,
      userId: this.state.bills.userId,
      kosten: this.state.bills.kosten,
      beschreibung: this.state.bills.beschreibung
    };
    let url =  `http://localhost9000/bills/updateBill/${this.state.bills.id}`;
    axios
      .put(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Die Rechnung wurde aktualisiert.");
    window.location.reload();
  };

  handlePriceChange = e => {
    this.setState({ bills: { kosten: e.target.value } });
  };

  handleDescriptionChange = e => {
    this.setState({ bills: { beschreibung: e.target.value } });
  };

  handleNameChange = e => {
    this.setState({ bills: { name: e.target.value } });
  };

  handleCategoryChange = e => {
    this.setState({bills: {catId: e.target.value}});
  }
  //Variable noch gehardcodet in der URL
  fetchBills() {
    fetch(`/bills/1`)
      .then(res => res.json())
      .then(bill => this.setState({ bills: bill }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchBills();
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Rechnungsdaten.<br></br> Sie können die Rechnung
            jetzt bearbeiten.
            <br></br>
            <br></br>
            Rechnungs-ID: {this.state.bills.id}
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.bills.name}
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.bills.kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text" 
            name="description"
            value={this.state.bills.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <label>Kategorie: </label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category"
            value={this.state.bills.catId}
            onChange={this.handleCategoryChange}
          >
            <option value=" ">/</option>
            <option value="1" >Versicherung</option>
            <option value="2">Entertainment</option>
            <option value="3">Lebensmittel</option>
            <option value="4">Verkehr</option>
            <option value="5">Bildung</option>
          </select>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung bestätigen"
          />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default EditBill;
