import React from "react";
import ImpressumPage from "./components/Pages/ImpressumPage";
import NavigationBar from "./components/NavigationBar";
import ContentContainer from "./components/ContentContainer";
import Footer from "./components/Footer";
import "./App.css";
import BrowserRouter from 'react-router-dom';

class ContractManager extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      apiResponse: [],
      loggedIn: true,
      users: [],
      user: {
        id: 1,
        username: "username",
        firstName: "firstname",
        name: "name",
        email: "email@email.de",
        password: "...",
        signUpDate: "2019-12-12"
      },
      pageTitle: [
        "Home",
        "Rechnungen",
        "Verträge",
        "Kategorien",
        "Impressum",
        "Kontakt",
        "Profil"
      ],
      currentPage: 0
    };
  }

  callTestAPI() {
    fetch("/testAPI")
      .then(res => res.json())
      .then(res => this.setState({ apiResponse: res }))
      .catch(err => err);
  }

  fetchUsers() {
    fetch("/users")
      .then(res => res.json())
      .then(users => this.setState({ users: users }))
      .catch(err => err);
  }
  handlePageChange = pageId => {
    let oldPage = this.state.currentPage;

    if (pageId !== oldPage) {
      this.setState({ currentPage: pageId });
      alert(this.state.currentPage + oldPage + pageId);
    } else {
      return;
    }
  };

  editUserData(key, newValue) {
    this.setState({ key: newValue });
  }

  componentDidMount() {
    this.fetchUsers();
  }

  render() {
    return (
      <BrowserRouter>
      <div className="App">
        <header>
          <NavigationBar pageChanged={this.handlePageChange} />
        </header>
        <main>
          <ContentContainer
            editUserData={this.editUserData}
            loggedIn={this.state.loggedIn}
            userId={this.state.userId}
            title={this.state.pageTitle[this.state.currentPage]}
            currentPage={this.state.currentPage}
            user={this.state.user}
          />
          <div>
            {this.state.users.map(user => {
              return (
                <div key={user.id}>
                  <p>
                    username: {user.username} userid: {user.id}
                  </p>
                </div>
              );
            })}
          </div>
          <ImpressumPage />
        </main>
        <Footer changePage={this.handlePageChange} />
      </div>
      </BrowserRouter>
    );
  }
}

export default ContractManager;
