import React from "react";

//Schleife bauen die einmal durch contracts und bills iteriert und jeweils die kosten addiert und als neue Variable zurück gibt
const sumContractCosts = contracts => {
  let contractSum = 0;
  contracts.contracts.map(contract => {
    contractSum = contractSum + contracts.kosten;
    return <div>{contractSum}</div>;
  });
};

const sumBillCosts = bills => {
  let billSum = 0;
  bills.map(bill => {
    billSum = billSum + bill.kosten;
    return <div>{billSum}</div>;
  });
  return billSum;
};

const sumObj = props => {
  let contracts = props.contracts;
  let bills = props.bills;

  return (
    <div className="sumObj">
      <div>
        <form>
          <h3>{props.title}</h3>
          <ul className="HomeList">
            <li>{sumBillCosts(bills)}€</li>
          </ul>
        </form>
      </div>
    </div>
  );
};

export default sumObj;
