import React from "react";
import "./styles/Profile.css";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: {},
      user: {
        id: 1,
        username: "username",
        firstName: "firstname",
        name: "name",
        email: "email@email.de",
        password: "...",
        signUpDate: "2019-12-12"
      }
    };
  }

  fetchUsers() {
    fetch("/users")
      .then(res => res.json())
      .then(users => this.setState({ users: users }))
      .catch(err => err);
  }
  componentDidMount() {
    this.fetchUsers();
  }

  // noch nicht fertig
  handleInput = props => {
    this.props.editUserData(props.key, props.event.target.value);
  };

  handleEdit = (type, placeholderText, key) => {
    return (
      <div>
        <label>
          <input
            onChange={this.handleInput(key)}
            type={type}
            placeholder={placeholderText}
          />
        </label>
      </div>
    );
  };
  //

  render() {
    return (
      <div className="profile">
        <section className="profileSection">
          <ul>
            <li>
              {" "}
              <div className="pDiv">
                <div>Ihre User ID: {this.state.user.id}</div>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Username: {this.state.user.username}</div>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Name: {this.state.user.name}</div>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>Vorname: {this.state.user.firstName}</div>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>E-Mail: {this.state.user.email}</div>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>angemeldet seit: {this.state.user.signUpDate}</div>
              </div>
            </li>
          </ul>
        </section>
        <button className="button3">Edit</button>
      </div>
    );
  }
}

export default Profile;
