import React from "react";
import Bill from "./Bill";
import "./styles/Contracts.css";
import "./styles/GridLayout.css";

class BillContainer extends React.Component {
  constructor(props){
    super(props)
    this.state= {
      bills: []
    }
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }

  componentDidMount() {

    this.fetchBills();
  }

  render() {
    return (
      <div className="contractContainer">
        {this.state.bills.map(bill => {
          return (
            <Bill
              key={bill.id}
              className="col-5"
              name={bill.name}
              id={bill.id}
              price={bill.kosten}
              description={bill.beschreibung}
            />
          );
        })}
      </div>
    );
  }
}

export default BillContainer;
