import React from "react";
import axios from "axios";
import Bill from "./Bill";
import "./styles/Navigationbar.css";
import "./styles/FilterSelect.css";

class Searchbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchContent: "",
      bills: []
    };
  }

  fetchSearchResults = e => {
    e.preventDefault();
    let obj = {
      name: this.state.searchContent
    };

    let url = "http://localhost:9000/bills/search";
    axios
      .post(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));
 
  };

  handleOnChange = e => {
    this.setState({ searchContent: e.target.value });
  };

  render() {
    return (
      <React.Fragment>
        <form className="searchbar" onSubmit={this.fetchSearchResults}>
          <input
            className="input1"
            id="search"
            label="Suche"
            type="text"
            value={this.state.searchContent}
            onChange={this.handleOnChange}
            placeholder="Was suchen Sie?"
          ></input>
          <input className="button2" type="submit" value="Go" />
        </form>

        {this.state.bills.map(bill => {
          return (
            <Bill
              name={this.props.name}
              id={this.props.id}
              price={this.props.price}
              description={this.props.description}
              catId1={this.props.catId1}
              catId2={this.props.catId2}
              catId3={this.props.catId3}
              categories={this.props.categories}
            />
          );
        })}
      </React.Fragment>
    );
  }
}

export default Searchbar;
