import React from "react";
import BillsByCategory from "./BillsByCategory";

import "../styles/Home.css";
import "../styles/AddForm.css";
import "../styles/Contracts.css";

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      content: null,
      contracts: [],
      selectId: null
    };
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }
  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }

  filterSelectHandler = e => {
    this.setState({ selectId: e.target.value });
  };

  handleButtonClick = e => {
    this.setState({
      content: (
        <BillsByCategory
          catId={this.state.selectId}
          bills={this.state.bills}
          categories={this.props.categories}
        />
      )
    });
  };

  render() {
    return (
      <div className="content-container">
        <div className="content-container">
          <label className="labelCat">Nach welcher Kategorie filtern? </label>
          <form className="BuildControls">
            <select
              className="inputAdd"
              onChange={this.filterSelectHandler}
              name="Sortieren nach"
            >
              {this.props.categories.map(category => {
                if (category.id === 1) {
                  return (
                    <option key={category.id} value={category.id}>
                      ohne Kategorie
                    </option>
                  );
                } else {
                  return (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  );
                }
              })}
            </select>
          </form>
          <button className="button4" onClick={this.handleButtonClick}>
            Go
          </button>
        </div>
        <div>{this.state.content}</div>
      </div>
    );
  }
}

export default FilterCategories;
