import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";
import "./styles/Navigationbar.css";
import FilterMinMax from "./FilterMinMax";
import BillContainer from "./BillContainer";

class FilterSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      value1: 1,
      renderedMinMax: <FilterMinMax type="number" />,
      newBillContainer: null
    };
  }

  filterSelectHandler = e => {
    console.log(e.target.value);
    this.setState({ value1: e.target.value });
  };

  filterBills = () => {
    let filteredBills = new Array();
    console.log("VALUE " + this.state.value1);
    this.props.bills.map(bill => {
      console.log("TEST");
      console.log(bill);
      console.log("KATEGORIE 1: " + bill.catId1 + "VALUE " + this.state.value1);
      console.log("KATEGORIE 2: " + bill.catId2 + "VALUE " + this.state.value1);
      console.log("KATEGORIE 3: " + bill.catId3 + "VALUE " + this.state.value1);
      if (
        bill.catId1 == this.state.value1 ||
        bill.catId2 == this.state.value1 ||
        bill.catId3 == this.state.value1
      ) {
        filteredBills.push(bill);
        console.log("HINZUGEFUGT" + bill);
      }
    });
    console.log(filteredBills);
    this.setState({ bills: filteredBills });
    this.setState({
      newBillContainer: (
        <BillContainer
          bills={filteredBills}
          categories={this.props.categories}
        />
      )
    });
  };

  render() {
    return (
      <div className="topBar">
        <div>
          <label> Filtern nach: </label>
          <select
            className="filterInput"
            onChange={this.filterSelectHandler}
            name="Sortieren nach"
          >
            {this.props.categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <button onClick={this.filterBills} className="button4">
          Go
        </button>
       
        <div className="sortedBillsDiv">{this.state.newBillContainer}</div>
        <div>{this.state.renderedMinMax}</div>
      </div>
    );
  }
}

export default FilterSelect;
