import React from "react";
import Bill from "../Bill";
import "../styles/Contracts.css";

class BillsByCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      catId: this.props.catId,
      bills: this.props.bills,
      contracts: this.props.contracts
    };
  }

  render() {
    return (
      <div className="contractContainer">
        {this.props.bills.map(bill => {
          if (
            bill.catId1 === this.state.catId ||
            bill.catId2 === this.state.catId ||
            bill.catId3 === this.state.catId
          ) {
            return (
              <Bill
                name={bill.name}
                id={bill.id}
                price={bill.price}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={this.props.categories}
              />
            );
          } else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default BillsByCategory;
