import React from "react";
import axios from "axios";
import Bill from "./Bill";
import "./styles/Navigationbar.css";

class Searchbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchContent: "",
      bills: []
    };
  }

  fetchSearchResults = e => {
    e.preventDefault();
    let obj = {
      message: this.state.searchContent
    };

    let url = "http://localhost:9000/bills/search";
    axios
      .post(url, obj)
      .then(res => this.setState({ bills: res.data }))
      .catch(e => console.log(e));

    console.log(this.state.bills);
  };

  render() {
    return (
      <React.Fragment>
        <form className="searchbar" onSubmit={this.fetchSearchResults}>
          <input
            className="input1"
            id="search"
            label="Suche"
            type="text"
            placeholder="Was suchen Sie?"
          ></input>
        </form>
        <div className="sortedBillsDiv">
          {this.state.bills.map(bill => {
            return (
              <Bill
                name={this.props.name}
                id={this.props.id}
                price={this.props.price}
                description={this.props.description}
                catId1={this.props.catId1}
                catId2={this.props.catId2}
                catId3={this.props.catId3}
                categories={this.props.categories}
              />
            );
          })}
        </div>
      </React.Fragment>
    );
  }
}

export default Searchbar;
