import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class EditContract extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contracts: {
        id: "",
        userId: "",
        laufzeitBis: "",
        name: "",
        kosten: "",
        beschreibung: ""
      },
      contractId: this.props.location.state
    };
  }
  // PUT REQUEST KLAPPT NOCH NICHT
  handleEditSubmit = e => {
    e.preventDefault();
    let obj = {
      id: this.state.contracts.id,
      userId: this.state.contracts.userId,
      name: this.state.contracts.name,
      laufzeitBis: this.state.contracts.laufzeitBis,
      kosten: this.state.contracts.kosten,
      beschreibung: this.state.contracts.beschreibung
    };
    let url = "http://localhost9000/contracts/updateContract";
    axios
      .put(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Der Vertrag wurde aktualisiert.");
    window.location.reload();
  };

  handlePriceChange = e => {
    this.setState({ contracts: { kosten: e.target.value } });
  };

  handleDescriptionChange = e => {
    this.setState({ contracts: { beschreibung: e.target.value } });
  };

  handleNameChange = e => {
    this.setState({ contracts: { name: e.target.value } });
  };
  //Variable noch gehardcodet in der URL
  fetchContracts() {
    fetch(`/contracts/1`)
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchContracts();
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Vertragssdaten.<br></br> Sie können den Vertrag jetzt
            bearbeiten.
            <br></br>
            <br></br>
            Vertrags-ID: {this.state.contracts.id}
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.contracts.name}
            onChange={this.handleNameChange}
          ></input>
          <label>Ende: </label>
          <input
            className="inputAdd"
            type="date"
            name="laufzeitBis"
            value={this.state.contracts.laufzeitBis}
            onChange={this.handleDateChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.contracts.kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            value={this.state.contracts.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input> 
          <label>Kategorie:</label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category"
            
            onChange={this.handleCategoryChange}
          >
            <option value={""}>/</option>
            <option value="1">Versicherung</option>
            <option value="2">Entertainment</option>
            <option value="3">Lebensmittel</option>
            <option value="4">Verkehr</option>
            <option value="5">Bildung</option>
          </select>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung bestätigen"
          />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default EditContract;
