import React from "react";
import axios from "axios";
import "./styles/Contracts.css";
import "./styles/BuildControls.css";
import "./styles/Navigationbar.css";

const ContractData = props => {
  return (
    <React.Fragment>
      <p>
        <b>Name:</b>
        <br /> {props.name}
      </p>
      <p>
        <b>ID:</b>
        <br />
        {props.id}
      </p>
      <p>
        <b>Preis: </b>
        <br />
        {props.price}€
      </p>
      <p>
        <b>Beschreibung: </b>
        <br />
        {props.description}
      </p>
    </React.Fragment>
  );
};

class Bill extends React.Component {
  handleDeleteBill = e => {
    e.preventDefault();
    let obj = {
      id: this.props.id
    };

    let url = "http://localhost:9000/bills/deleteBill";
    axios
      .delete(url, { id: obj.id })
      .then(res => console.log(res))
      .catch(e => console.log(e));
    alert(`Die Rechnung mit der ID ${obj.id} wurde gelöscht.`);
    this.setState();
  };

  render() {
    return (
      <div className="contractBox">
        <div>
          <ContractData
            name={this.props.name}
            id={this.props.id}
            price={this.props.price}
            description={this.props.description}
          ></ContractData>
          <div className="contractControl">
            <div className="toolbarNavigationItems">
              <ul>
                <li>
                  <button onClick={this.handleDeleteBill} className="button1">
                    Delete
                  </button>
                </li>
                <li>
                  <button className="button1">Edit</button>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Bill;
