import React from "react";
import "./styles/ContentContainer.css";
import "./styles/Forms.css";
import "./styles/GridLayout.css";
import "./styles/Profile.css";
import HomePage from "./Pages/HomePage";
import ContractPage from "./Pages/ContractPage";
import BillPage from "./Pages/BillPage";
import CategoryPage from "./Pages/CategoryPage";
import ProfilePage from "./Pages/ProfilePage";


class ContentContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: true,
      userId: "",
      title: this.props.title,
      currentPage: this.props.currentPage,
      content: "",
      user: this.props.user,
      contracts: [],
      bills: [],
      categories: []
    };
    this.pageChanger = this.pageChanger.bind(this);
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }

  fetchCategories() {
    fetch("/categories")
      .then(res => res.json())
      .then(categories => this.setState({ categories: categories }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
    this.pageChanger(this.state.currentPage);
  }


  //funktioniert noch nicht
  pageChanger() {
    let page = <div id="content"></div>;
    switch (this.props.currentPage) {
      case "0":
        page = <HomePage title={this.state.title} />;
        this.setState({ content:  page  });
        break;
      case "1":
        page = <BillPage title={this.state.title} bills={this.state.bills} />;
        this.setState({ content:  page  });
        break;
      case "2":
        page = (
          <ContractPage
            title={this.state.title}
            contracts={this.state.contracts}
          />
        );
        this.setState({ content:  page  });
        break;
      case "3":
        page = (
          <ContractPage
            title={this.state.title}
            contracts={this.state.contracts}
          />
        );
        this.setState({ content:  page  });
        break;
      case "4":
        page = (
          <ContractPage
            title={this.state.title}
            contracts={this.state.contracts}
          />
        );
        this.setState({ content:  page  });
        break;
      case "5":
        page = (
          <ContractPage
            title={this.state.title}
            contracts={this.state.contracts}
          />
        );
        this.setState({ content:  page  });
        break;
      case "6":
        page = (
          <ContractPage
            title={this.state.title}
            contracts={this.state.contracts}
          />
          
        );
        this.setState({ content:  page  });
        break;
        default: page =  "failed";
    }
    
  }

  render() {
    
    return (
      <div>

        {this.state.currentPage === 0 && <HomePage title="Home" contracts={this.state.contracts} bills={this.state.bills} />}
        
        {this.state.currentPage === 1  && <BillPage title="Bills" bills={this.state.bills} />}
        <ContractPage title="Verträge" contracts={this.state.contracts} />
        <CategoryPage title="Kategorien" />
        <ProfilePage title="Ihr Profil" userData={this.state.user} editUserData={this.props.editUserData}/>
        {this.state.content}
      </div>
    );
  }
}

export default ContentContainer;
