import React from "react";
import ImpressumPage from "./components/Pages/ImpressumPage";
import NavigationBar from "./components/NavigationBar";
import ContentContainer from "./components/ContentContainer";
import "./App.css";
import { BrowserRouter } from "react-router-dom";
import AuthContainer from "./components/Auth/AuthContainer";
import axios from "axios";

class ContractManager extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //content: [
      //  <AuthContainer />,
      //  <ContentContainer
      //    editUserData={this.editUserData}
      //    loggedIn={this.state.loggedIn}
      //    userId={this.state.userId}
      //    user={this.state.user}
      //  /> 
      //],
      content: <div></div>,
      isLoggedIn: false,
      users: [],
      user: {
        id: 1,
        username: "username",
        firstName: "firstname",
        name: "name",
        email: "email@email.de",
        password: "...",
        signUpDate: "2019-12-12"
      }
    };
    this.handleLoginClick = this.handleLoginClick.bind(this);
    this.handleLogoutClick = this.handleLogoutClick.bind(this);

  }

  callTestAPI() {
    fetch("/testAPI")
      .then(res => res.json())
      .then(res => this.setState({ apiResponse: res }))
      .catch(err => err);
  }


  editUserData(key, newValue) {
    this.setState({ key: newValue });
  }

  componentDidMount() {
    this.renderContent();

  }

  handleLoginClick(loggedIn) {
    console.log(loggedIn);
    this.setState({ isLoggedIn: loggedIn });
  }

  handleLogoutClick() {
    this.setState({ isLoggedIn: false });
  }


  handleMailChange = e => {
    this.setState({ email: e.target.value });
  };

  handlePasswordChange = e => {
    this.setState({ password: e.target.value });
  };

  handleRegisterUserClick = () => {
    this.setState({ content: <RegisterForm></RegisterForm> });
  };

  loginUser = () => {
    let obj = {
      email: this.state.email,
      password: this.state.password
    };
    let url = "http://localhost:9000/user1/api/login";
    axios
      .post(url, obj)
      .then(res => () => this.props.handleLoginClick(res.data.login))
      .catch(e => console.log(e));

      console.log(this.state.result);
  };
  renderContent() {
    if (this.state.isLoggedIn == false) {
      this.setState({
        content: <AuthContainer handleLoginClick={this.handleLoginClick} />
      });
    } else {
      this.setState({
        content: (
          <ContentContainer
            editUserData={this.editUserData}
            loggedIn={this.state.isLoggedIn}
            userId={this.state.userId}
            user={this.state.user}
          />
        )
      });
    }
  }


  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <header>
            <NavigationBar pageChanged={this.handlePageChange} />
          </header>
          <section></section>
          <main>{this.state.content}</main>
          <footer>
            <ImpressumPage />
          </footer>
        </div>
      </BrowserRouter>
    );
  }
}

export default ContractManager;
